import { Link } from "react-router-dom";
import { useEffect } from "react"
import * as BooksAPI from "./BooksAPI"
import { useState } from "react";
import Select from "react-select";

const ListBooks = ({books}) => {

    console.log("this is the listbooks component: ")
    console.log(books)

    const options = [
        { value: 'currentlyReading', label: 'Currently Reading' },
        { value: 'read', label: 'Read' },
        { value: 'wantToRead', label: 'Want To Read' },
        { value: 'none', label: 'None' }
      ]

    // To be used to manage book state and pass to the API
    const [value, setValue] = useState('');

    return(
        <div className="list-books">
          <div className="list-books-title">
            <h1>MyReads</h1>
          </div>
          <div className="list-books-content">
            <div>
              <div className="bookshelf">
                <h2 className="bookshelf-title">Currently Reading</h2>
                <div className="bookshelf-books">
                  <ol className="books-grid">
                    {
                        books.filter(book => book.shelf == "currentlyReading").map(filteredBook => (
                            <li>
                                {console.log(filteredBook.shelf)}
                                <div className="book">
                                    <div className="book-top">
                                    <div
                                        className="book-cover"
                                        style={{
                                        width: 128,
                                        height: 193,
                                        backgroundImage: 
                                            'url(' + filteredBook.imageLinks.thumbnail + ')',
                                        }}
                                    ></div>
                                    <div className="book-shelf-changer">
                                    <Select
                                        defaultValue={filteredBook.shelf}
                                        name="shelves"
                                        options={options}
                                        className="selecter"
                                        classNamePrefix="selecter"
                                    />
                                    </div>
                                    </div>
                                    <div className="book-title">{filteredBook.title}</div>
                                    {filteredBook.authors.map(author => (
                                        <div className="book-authors">{author}</div>
                                    ))}
                                </div>
                            </li>
                        )
                    )}
                  </ol>
                </div>
              </div>
              <div className="bookshelf">
                <h2 className="bookshelf-title">Want to Read</h2>
                <div className="bookshelf-books">
                  <ol className="books-grid">
                  {
                        books.filter(book => book.shelf == "wantToRead").map(filteredBook => (
                            <li>
                                <div className="book">
                                    <div className="book-top">
                                    <div
                                        className="book-cover"
                                        style={{
                                        width: 128,
                                        height: 193,
                                        backgroundImage: 
                                            'url(' + filteredBook.imageLinks.thumbnail + ')',
                                        }}
                                    ></div>
                                    <div className="book-shelf-changer">
                                    <Select
                                        defaultValue={filteredBook.shelf}
                                        name="shelves"
                                        options={options}
                                        className="selecter"
                                        classNamePrefix="selecter"
                                    />
                                    </div>
                                    </div>
                                    <div className="book-title">{filteredBook.title}</div>
                                    {filteredBook.authors.map(author => (
                                        <div className="book-authors">{author}</div>
                                    ))}
                                </div>
                            </li>
                        )
                    )}
                  </ol>
                </div>
              </div>
              <div className="bookshelf">
                <h2 className="bookshelf-title">Read</h2>
                <div className="bookshelf-books">
                  <ol className="books-grid">
                  {
                        books.filter(book => book.shelf == "read").map(filteredBook => (
                            <li>
                                                                {console.log(filteredBook.shelf)}

                                <div className="book">
                                    <div className="book-top">
                                    <div
                                        className="book-cover"
                                        style={{
                                        width: 128,
                                        height: 193,
                                        backgroundImage: 
                                            'url(' + filteredBook.imageLinks.thumbnail + ')',
                                        }}
                                    ></div>
                                    <div className="book-shelf-changer">
                                    <Select
                                        defaultValue={filteredBook.shelf}
                                        name="shelves"
                                        options={options}
                                        className="selecter"
                                        classNamePrefix="selecter"
                                    />
                                    </div>
                                    </div>
                                    <div className="book-title">{filteredBook.title}</div>
                                    {filteredBook.authors.map(author => (
                                        <div className="book-authors">{author}</div>
                                    ))}
                                </div>
                            </li>
                        )
                    )}
                  </ol>
                </div>
              </div>
            </div>
          </div>
          <Link to="/search" className="open-search">
            Add a book
          </Link>
        </div>
    )
}
export default ListBooks;
