          //  b.shelf = shelf;
          //}
        })
        //setBooks(updatedBooks);
        console.log(book);
      };
  }, []);

  //edit


  return (
    <Routes>
      <Route  exact path="/"  element={<ListBooks books={books} options={options} changeBookShelf={changeBookShelf}/>}/>
      <Route path="/Search" element={<Search/>}/>
    </Routes>
  );
}

export default App;
