import Select from "react-select";
import { useEffect, useState } from "react";

const Book = ({currBook, options, defaultValues, changeBookShelf}) => {

    const [state, setState] = useState('');
    const [defaultVal, setDefaultVal] = useState([defaultValues]);

    //https://codesandbox.io/s/serene-snow-179rdh?file=/src/select.js
    useEffect(() => {
        setState(defaultVal);
    }, [defaultVal]);

    const changeHandler = (value) => {
        if(changeBookShelf){
            changeBookShelf(value)
        }
    }

    return (
        <div className="book">
            <div className="book-top">
                <div
                    className="book-cover"
                    style={{
                        width: 128,
                        height: 193,
                        backgroundImage: 
                            'url(' + currBook.imageLinks.thumbnail + ')',
                    }}
                    ></div>
                    <div className="book-shelf-changer">
                        <Select
                        defaultValue={defaultVal}
                        name="shelves"
                        options={options}
                        className="selecter"
                        classNamePrefix="selecter"
                        onChange={changeHandler}
                         />
                    </div>
                </div>
                <div className="book-title">{currBook.title}</div>
                {currBook.authors.map(author => (
                <div className="book-authors">{author}</div>
            ))}
        </div>
    )
}

export default Book;
