import Select from "react-select";
import { useEffect, useState } from "react";

const Book = ({currBook, changeBookShelf}) => {

    const [state, setState] = useState('');
    const [defaultVal, setDefaultVal] = useState([defaultValues]);

    //https://codesandbox.io/s/serene-snow-179rdh?file=/src/select.js
    useEffect(() => {
        setState(defaultVal);
    }, [defaultVal]);

    const changeHandler = (value) => {
        
    }

    return (
        <div className="book">
            <div className="book-top">
                <div
                    className="book-cover"
                    style={{
                        width: 128,
                        height: 193,
                        backgroundImage: 
                            'url(' + currBook.imageLinks.thumbnail + ')',
                    }}
                    ></div>
                    <div className="book-shelf-changer">
                        <Select
                        defaultValue={defaultVal}
                        name="shelves"
                        options={options}
                        className="selecter"
                        classNamePrefix="selecter"
                        onChange={changeHandler}
                         />
                    </div>

                    <select value={this.state.value} onChange={this.handleChange} >
                    <option value="move" disabled>Move to...</option>
                    <option value="currentlyReading">Currently Reading</option>
                    <option value="wantToRead">Want to Read</option>
                    <option value="read">Read</option>
                    <option value="none">None</option>
                    </select>
                <div className="book-title">{currBook.title}</div>
                {currBook.authors.map(author => (
                <div className="book-authors">{author}</div>
                ))}
            </div>
        </div>
    )
}

export default Book;
