import { Link } from "react-router-dom";
import { useEffect } from "react"
import * as BooksAPI from "./BooksAPI"
import { useState } from "react";
import Select from "react-select";
import Book from "./Book";

const ListBooks = ({books}) => {

    console.log("this is the listbooks component: ")
    console.log(books)

    const options = [
        { value: 'currentlyReading', label: 'Currently Reading' },
        { value: 'read', label: 'Read' },
        { value: 'wantToRead', label: 'Want To Read' },
        { value: 'none', label: 'None' }
      ]

    // To be used to manage book state and pass to the API
    const [value, setValue] = useState('');
    const changeBookShelf = () => {

    }

    return(
        <div className="list-books">
          <div className="list-books-title">
            <h1>MyReads</h1>
            <span>An app for the Udacity Nanodegree in React Development.</span>
          </div>
          <div className="list-books-content">
            <div>
              <div className="bookshelf">
                <h2 className="bookshelf-title">Currently Reading</h2>
                <div className="bookshelf-books">
                  <ol className="books-grid">
                    {
                        books.filter(book => book.shelf == "currentlyReading").map(filteredBook => (
                            <li>
                                {console.log(filteredBook.shelf)}
                                <Book key={filteredBook.id} currBook={filteredBook} options={options} defaultValues={{value: "currentlyReading", label:"Currently Reading"}} changeBookShelf={(value)}/>
                            </li>
                        )
                    )}
                  </ol>
                </div>
              </div>
              <div className="bookshelf">
                <h2 className="bookshelf-title">Want to Read</h2>
                <div className="bookshelf-books">
                  <ol className="books-grid">
                  {
                        books.filter(book => book.shelf == "wantToRead").map(filteredBook => (
                            <li>
                                <Book currBook={filteredBook} options={options} defaultValues={{value: "wantToRead", label:"Want to Read"}}/>
                            </li>
                        )
                    )}
                  </ol>
                </div>
              </div>
              <div className="bookshelf">
                <h2 className="bookshelf-title">Read</h2>
                <div className="bookshelf-books">
                  <ol className="books-grid">
                  {
                        books.filter(book => book.shelf == "read").map(filteredBook => (
                            <li>
                                <Book currBook={filteredBook} options={options} defaultValues={{value: "read", label:"Read"}}/>
                            </li>
                        )
                    )}
                  </ol>
                </div>
              </div>
            </div>
          </div>
          <Link to="/search" className="open-search">
            Add a book
          </Link>
        </div>
    )
}
export default ListBooks;
